      if (
        this.kycNationality// 国籍
        && this.certificateType1 // 证件类型1
        && this.certificateType1Value // 证件类型1 value
        && this.kycName // 名
        && this.kycFamilyName // 姓
        // && this.address // 地址
      ) { // 证件号码
        flag = false;
      }
      return flag;
