    // 国籍
    kycNationality() {
      let str = '';
      const current = this.countryRealMap[`+${this.kycPhone}`];
      if (this.kycPhone && current) {
        const val = current.value;
        [str] = val.split(' +');
      }
      return str;
    },
    // 验证类型 1为中国人 2为外国人
    kycType() {
      if (this.kycPhone) {
        if (this.kycPhone === '156') {
          return '1';
