  methods: {
    init() {
      if (this.$route.query) {
        if (this.$route.query.country) {
          this.kycPhone = this.$route.query.country;
          this.kycPhoneCode = this.$route.query.countryKeyCode;
        } else if (this.$route.query.countryKeyCode) {
          this.kycPhone = this.countryMap[`+${this.$route.query.countryKeyCode}`].code.replace('+', '');
          this.kycPhoneCode = this.$route.query.countryKeyCode;
        } else {
          this.kycPhone = '156';
          this.kycPhoneCode = '86';
        }
      }
      if (this.publicInfo !== null) {
        this.isOpenUploadImg = this.publicInfo.switch.is_open_upload_img;
      }
      if (this.userInfo) {
        this.authLevel = this.userInfo.authLevel;
        if (this.authLevel === 2 || this.authLevel === 3) {
          this.active = '1';
        } else if (this.authLevel === 0) {
          this.active = '3';
        } else {
          // this.authLevel为1 审核通过
          this.$router.push('/personal/userManagement');
        }
      }
      if (this.interfaceSwitch) {
        this.getLang();
      }
    },
    getLang() {
      this.axios({
        url: '/kyc/Api/getUploadImgCopywriting',
      }).then((data) => {
        if (data.code.toString() === '0') {
          if (data.data.language) {
            this.kycMetaText = data.data.language;
          }
        } else {
          this.$bus.$emit('tip', { text: data.msg, type: 'error' });
        }
      });
    },
    certificateType1Change({ code }) {
      this.certificateType1 = code;
      this.certificateType2Options = this.idAuthType.filter((item) => item.code
        !== this.certificateType1);
      this.idAuthType.forEach((item) => {
        if (item && item.code === code) {
          this.certificateType1NumPromptText = this.$t(`personal.kyc.value${code}`);
        }
      });
    },
    certificateType2Change({ code }) {
      this.certificateType2 = code;
      this.certificateType1Options = this.idAuthType.filter((item) => item.code
        !== this.certificateType2);
      this.idAuthType.forEach((item) => {
        if (item && item.code === code) {
          this.certificateType2NumPromptText = this.$t(`personal.kyc.value${code}`);
        }
      });
    },
    certificateTypeInputChange(value, name) {
      this[name] = value;
    },
    idAuthTypeChange({ code }) {
      this.certificateType = code;
    },
    uploadFinish(obj) {
      if (obj.name) {
        this.uploadName = obj.name;
      }
      if (obj.error) {
        this.$bus.$emit('tip', { text: obj.error, type: 'error' });
        return;
      }
      this[`${this.uploadName}Img`] = obj.url;
      this[`${this.uploadName}Loading`] = false;
      this[this.uploadName] = obj.fileName;
      if (
        this.firstPhotoImg
        // && this.secondPhotoImg
        // && this.thirdPhotoImg
        // && this.fourthPhotoImg
        // && this.fifthPhotoImg
        // && this.sixthPhotoImg
      ) {
        this.disabled2 = false;
      }
    },
    onChangeFileFun(obj) {
      if (obj.code && obj.code === 'maxSize') {
        this.$bus.$emit('tip', { text: this.$t('personal.exccAuthForm.text3'), type: 'error' });
      }
      this.uploadName = obj.name;
      this[`${this.uploadName}Loading`] = true;
    },
    inputChanges(value, name) {
      // 中国 - 姓名
      if (name === 'name') {
        this.checkValue2 = value;
      // 外国 - 姓
      } else if (name === 'kycFamilyName') {
        this.kycFamilyName = value;
      // 外国 - 名
      } else if (name === 'kycName') {
        this.kycName = value;
      // 证件号码
      } else {
        this.checkValue1 = value;
      }
    },
    checkFocus(name) {
      switch (name) {
        case 'email': {
          // email
          this.checkErrorFlag1 = false;
          this.errorText1 = '';
          break;
        }
        case '2': {
          // emailCode
          break;
        }
        default: {
          // newpassword again
        }
      }
    },
    btnLink(num) {
      if (num === 1) {
        this.active = '2';
      } else {
        this.loading = true;
        const datas = {};
        datas.info = {
          countryCode: `+${this.kycPhoneCode}`, // 电话编码
          numberCode: this.kycPhone, // 国家代码
          // certificateType: type, // 证件类型
          // certificateNumber: this.checkValue1, // 证件号
          userName: this.checkValue2 || '', // 中国 - 姓名
          // firstPhoto: this.firstPhoto,
          // secondPhoto: this.secondPhoto,
          // thirdPhoto: this.thirdPhoto,
          familyName: this.kycFamilyName || '', // 外国姓
          name: this.kycName || '', // 外国名

          certificateNumber: this.certificateType1Value || '', // 证件1号码
          certificateType: this.certificateType1 || '', // 证件类型 1
          firstPhoto: this.firstPhoto || '', // 证件1正面
          secondPhoto: this.secondPhoto || '', // 证件1背面

          certificateNumber2: this.certificateType2Value || '', // 证件2号码
          certificateType2: this.certificateType2 || '', // 证件2类型
          thirdPhoto: this.thirdPhoto || '', // 证件2正面
          fourthPhoto: this.fourthPhoto || '', // 证件2背面

          fifthPhoto: this.fifthPhoto || '', // 地址证明
          sixthPhoto: this.sixthPhoto || '', // 手持签名

          address: this.address || '', // 地址
        };
        this.$store.dispatch('authRealname', datas);
      }
    },
  },
