// 按钮
export default {
  name: 'c-noticeDialog',
  props: {
    // 展示变量
    showFlag: {
      default: false,
      type: Boolean
    },
    // 上边距  有警告文字时为20px
    paddingTop: {
      default: '30px',
      type: String
    },
    // 下边距
    paddingBottom: {
      default: '30px',
      type: String
    },
    // 提示文案
    titleText: {
      default: '',
      type: String
    },
    // 确认按钮的文案
    confirmText: {
      default: '',
      type: String
    },
    // 确认按钮的loading
    confirmLoading: {
      default: false,
      type: Boolean
    },
    // 确认按钮的disabled
    confirmDisabled: {
      default: false,
      type: Boolean
    },
    // 是否有下面option区域
    haveOption: {
      default: true,
      type: Boolean
    }
  },
  watch: {
    showFlag: function showFlag(v) {
      var _this = this;

      if (v) {
        this.$nextTick(function () {
          if (_this.$refs.dialog.offsetHeight % 2 !== 0) {
            var str = "".concat(_this.$refs.dialog.offsetHeight + 1, "px");
            _this.$refs.dialog.style.height = str;
          }
        });
      }
    }
  },
  computed: {
    confirmTextProps: function confirmTextProps() {
      if (this.confirmText.length) {
        return this.confirmText;
      } // 确定


      return this.$t('components.dialog.confirmText');
    },
    bodyStyle: function bodyStyle() {
      return {
        paddingTop: this.paddingTop,
        paddingBottom: this.paddingBottom
      };
    }
  },
  methods: {
    close: function close() {
      this.$emit('close');
    },
    confirm: function confirm() {
      this.$emit('confirm');
    }
  }
};
