export default {
  name: 'c-loading',
  props: {
    // 尺寸
    size: {
      default: '30',
      type: String
    },
    // class
    className: {
      default: 'a-12-bd',
      type: String
    },
    colorName: {
      default: 'b-4-cl',
      type: String
    }
  },
  computed: {
    // 容器盒子style
    contentStyle: function contentStyle() {
      return {
        width: "".concat(this.size, "px"),
        height: "".concat(this.size, "px")
      };
    },
    // 扩出盒子style
    leftStyle: function leftStyle() {
      var bw = Math.round(this.size / 10);
      var w = this.size - 2 * bw;
      return {
        width: "".concat(w, "px"),
        height: "".concat(w, "px"),
        borderWidth: "".concat(bw, "px"),
        borderLeft: "".concat(bw, "px solid transparent"),
        borderBottom: "".concat(bw, "px solid transparent")
      };
    },
    // 收起盒子style
    rightStyle: function rightStyle() {
      var bw = Math.round(this.size / 10);
      var w = this.size - 2 * bw;
      return {
        width: "".concat(w, "px"),
        height: "".concat(w, "px"),
        borderWidth: "".concat(bw, "px"),
        borderRight: "".concat(bw, "px solid transparent"),
        borderTop: "".concat(bw, "px solid transparent")
      };
    }
  }
};
