import "core-js/modules/web.dom.iterable";
import { nul, imgMap, colorMap } from '@/utils';
export default {
  name: 'blackList',
  watch: {
    otcPersonRelationship: function otcPersonRelationship(_otcPersonRelationship) {
      if (_otcPersonRelationship !== null) {
        this.loading = false;
        this.dataProcessin(_otcPersonRelationship.data);
      }
    },
    otcUserContactsRemove: function otcUserContactsRemove(_otcUserContactsRemove) {
      if (_otcUserContactsRemove !== null) {
        if (_otcUserContactsRemove.text === 'success') {
          this.$bus.$emit('tip', {
            text: _otcUserContactsRemove.msg,
            type: 'success'
          });
          this.$store.dispatch('resetType');
          this.dialogFlag = false;
          var info = {
            relationType: 'BLACKLIST',
            page: this.page,
            pageSize: this.pageSize
          };
          this.$store.dispatch('otcPersonRelationship', info);
          this.loading = true;
        } else {
          this.$bus.$emit('tip', {
            text: _otcUserContactsRemove.msg,
            type: 'error'
          });
          this.$store.dispatch('resetType');
        }
      }
    }
  },
  computed: {
    otcPersonRelationship: function otcPersonRelationship() {
      return this.$store.state.personal.otcPersonRelationship;
    },
    otcUserContactsRemove: function otcUserContactsRemove() {
      return this.$store.state.fiatdeal.otcUserContactsRemove;
    }
  },
  methods: {
    init: function init() {
      var info = {
        relationType: 'BLACKLIST',
        page: this.page,
        pageSize: this.pageSize
      };
      this.$store.dispatch('otcPersonRelationship', info);
      this.loading = true;
    },
    dialogClose: function dialogClose() {
      this.dialogFlag = false;
    },
    dialogConfirm: function dialogConfirm() {
      var info = {
        friendId: this.uid
      };
      this.$store.dispatch('otcUserContactsRemove', info);
    },
    closeData: function closeData(name, id) {
      this.dialogFlag = true;
      this.uid = id;
    },
    pagechange: function pagechange(page) {
      this.page = page;
      var info = {
        relationType: 'BLACKLIST',
        page: this.page,
        pageSize: this.pageSize
      };
      this.$store.dispatch('otcPersonRelationship', info);
      this.loading = true;
    },
    dataProcessin: function dataProcessin(data) {
      var _this = this;

      this.count = data.count;
      var list = [];
      data.relationshipList.forEach(function (obj) {
        list.push({
          id: obj.userId,
          data: [obj.otcNickName, "".concat(nul(obj.creditGrade, 100), "%"), obj.completeOrders, obj.completeOrders, [{
            type: 'button',
            // 'link' ' button' ,'html', 'label', 'icon' ‘str’
            text: _this.$t('personal.blackList.closeText'),
            iconClass: [''],
            eventType: 'close',
            classes: ['']
          }]]
        });
      });
      this.dataList = list;
    }
  },
  data: function data() {
    return {
      imgMap: imgMap,
      colorMap: colorMap,
      titleText: this.$t('personal.dialog.blackPrompt'),
      // table loading
      loading: false,
      uid: '',
      // table相关
      columns: [{
        title: this.$t('personal.blackList.columns')[0],
        align: 'left',
        width: '100px',
        classes: ''
      }, {
        title: this.$t('personal.blackList.columns')[1],
        align: 'right',
        width: '100px'
      }, {
        title: this.$t('personal.blackList.columns')[2],
        align: 'right',
        width: '100px'
      }, {
        title: this.$t('personal.blackList.columns')[3],
        align: 'right',
        width: '100px'
      }, {
        title: this.$t('personal.blackList.columns')[4],
        align: 'right',
        width: '150px'
      }],
      dataList: [],
      cellHeight: 55,
      headHeight: 30,
      lineNumber: 10,
      classes: '',
      headClasses: '',
      bodyClasses: '',
      lineClass: '',
      // page相关
      count: 0,
      page: 1,
      pageSize: 10,
      // 弹框
      dialogFlag: false,
      dialogConfirmLoading: false,
      dialogConfirmDisabled: false
    };
  }
};
