import { fixInput } from '@/utils'; // import Utils from '@/utils/coUtils.js';

export default {
  name: 'form-input',
  props: {
    isMarket: {
      default: false,
      type: Boolean
    },
    fix: {
      default: 2,
      type: [Number, String]
    },
    default: {
      default: '',
      type: [Number, String]
    },
    unit: {
      default: '',
      type: String
    },
    title: {
      default: '',
      type: String
    },
    placeHolder: {
      default: '',
      type: String
    },
    haveMarket: {
      default: false,
      type: Boolean
    },
    isVol: {
      default: false,
      type: Boolean
    },
    isPercentage: {
      default: '',
      type: [Number, String]
    },
    isError: {
      default: null,
      type: [Number, String]
    },
    promptText: {
      default: '',
      type: String
    }
  },
  data: function data() {
    return {
      value: '',
      inputClass: '',
      isFocus: false
    };
  },
  computed: {
    coinList: function coinList() {
      if (this.$store.state.baseData && this.$store.state.baseData.market) {
        return this.$store.state.baseData.market.coinList;
      }

      return null;
    },
    coinUnit: function coinUnit() {
      return this.$store.state.swap.coinUnit;
    },
    valueUnit: function valueUnit() {
      return this.$store.state.swap.valueUnit;
    }
  },
  watch: {
    default: function _default(val) {
      this.value = val;
    },
    isError: function isError(val) {
      this.inputClass = val ? 'a-19-bd' : 'a-3-bd';
    },
    isMarket: function isMarket(val) {
      var _this = this;

      if (!val) {
        setTimeout(function () {
          _this.$refs.formInput.focus();
        });
      }
    }
  },
  mounted: function mounted() {
    this.value = this.default;
    this.inputClass = this.isError ? 'a-19-bd' : 'a-3-bd';
  },
  methods: {
    focusInputClass: function focusInputClass(flag) {
      if (!this.isError) {
        this.inputClass = 'a-12-bd';

        if (flag) {
          this.isFocus = flag;
        }
      }
    },
    blurinputClass: function blurinputClass() {
      if (!this.isError) {
        this.isFocus = false;
        this.inputClass = 'a-3-bd';
      }
    },
    mouseoutInputClass: function mouseoutInputClass() {
      if (!this.isError && !this.isFocus) {
        this.inputClass = 'a-3-bd';
      }
    },
    selectMarket: function selectMarket() {
      this.$emit('selectMarket');
    },
    focusInput: function focusInput() {
      var _this2 = this;

      setTimeout(function () {
        _this2.$refs.formInput.focus();
      });
    },
    clearPercentage: function clearPercentage() {
      var _this3 = this;

      this.$emit('clearPercentage');
      setTimeout(function () {
        _this3.$refs.formInput.focus();
      });
    },
    newFixInput: function newFixInput(val, fix) {
      return fixInput(val, fix); // if (fix > 0) {
      //   return r
      // } else {
      //   return Utils.retainDecimals(val, { decimal: 0 })
      // }
    },
    handleInput: function handleInput(val) {
      var data;
      var valus = val;
      var str = val;

      if (str.length >= 10) {
        valus = str.substring(0, 12);
      }

      if (!this.isVol) {
        data = this.newFixInput(valus, this.fix);
      } else {
        data = this.volChange(valus);
      }

      this.value = data;
      this.$emit('tradeChangeValue', data);
    },
    // 量改变事件
    volChange: function volChange(val) {
      if (val) {
        return this.newFixInput(val, this.fix);
      }

      return '';
    }
  }
};
