  name: 'b2cMixins',
  data: function data() {
    return {
      alertFlag: false
    };
  },
  watch: {
    userInfoIsReady: {
      immediate: true,
      handler: function handler(v) {
        if (v) {
          this.canAlert();
        }
      }
    }
  },
