      var _this = this;

      if ((this.OpenGoogle || !this.enforceGoogleAuth && this.OpenMobile) && this.idAuth) {
        this.alertFlag = false;
      } else {
        setTimeout(function () {
          _this.alertFlag = true;
        }, 100);
      }
    }
