var bitWindHomeFuncton = function bitWindHome() {
  var offsetheight = document.documentElement.clientHeight; // 获取当前页面的高度、

  var small = offsetheight <= 800;
  var $container = document.getElementById('bitWind-content');
  var $page = document.getElementById('bitWind-page');
  var $blockItem = document.getElementsByClassName('block-item');
  var $navItem = document.getElementsByClassName('nav-item');
  var $app = document.getElementById('app');
  var $pageUl = document.getElementById('page-ul');
  var fullPage = 0; // 当前在第几页

  var fullPageNum = false; // 是否在滑动

  if ($pageUl && small) {
    $pageUl.style.height = "".concat(offsetheight - 100, "px");
  }

  if ($container && !small) {
    $container.style.height = "".concat(offsetheight, "px");
  }

  if ($blockItem && $blockItem.length) {
    for (var i = $blockItem.length - 1; i >= 0; i -= 1) {
      $blockItem[i].style.height = "".concat(offsetheight, "px");
    }
  } // 设置第一个为选中状态


  if ($navItem[fullPage]) $navItem[fullPage].classList.add('active');

  var setNavClass = function setNavClass() {
    if ($navItem && $navItem.length) {
      for (var _i = $navItem.length - 1; _i >= 0; _i -= 1) {
        $navItem[_i].classList.remove('active');

        if (fullPage < 6) {
          $navItem[fullPage].classList.add('active');
        }

        if (fullPage === 6) {
          $app.classList.add('app-block-item7');
        } else {
          $app.classList.remove('app-block-item7');
        }
      }
    }
  };

  var setPage = function setPage() {
    setNavClass();
    $page.style.transform = "translate3d(0px,-".concat(fullPage * offsetheight, "px, 0px)");
  };

  var scroll = function scroll(e) {
    var even = e || window.event;

    if (!fullPageNum) {
      if (even.wheelDelta < -10 && fullPage < $blockItem.length - 1) {
        fullPage += 1;
        fullPageNum = true;
        setPage();
        setTimeout(function () {
          fullPageNum = false;
        }, 1200);
      }

      if (even.wheelDelta > 10 && fullPage > 0) {
        fullPage -= 1;
        fullPageNum = true;
        setPage();
        setTimeout(function () {
          fullPageNum = false;
        }, 1200);
      }
    }
  };

  var clickPage = function clickPage() {
    if ($pageUl) {
      $pageUl.onclick = function navclck(e) {
        var index = parseInt(e.target.id, 0);

        if (small) {
          var navList = document.querySelector('#page-ul').querySelectorAll('.nav-item');
          document.querySelector('#bitWind-page').querySelectorAll('.block-item')[index].scrollIntoView();

          for (var _i2 = 0, len = navList.length; _i2 < len; _i2 += 1) {
            navList[_i2].classList.remove('active');
          }

          navList[index].classList.add('active');
        } else {
          document.querySelector('#page-ul').querySelectorAll('.nav-item');
          fullPage = index;
          setPage();
        }
      };
    }
  };

  clickPage();

  if (!small) {
    if (document.addEventListener) {
      document.addEventListener('DOMMouseScroll', scroll, false);
    }

    document.onmousewheel = scroll;
    window.onmousewheel = document.onmousewheel;
  }
};

window.bitWindHomeFuncton = bitWindHomeFuncton;
