import "core-js/modules/web.dom.iterable";
import "core-js/modules/es6.object.keys";
import "core-js/modules/es6.regexp.split";
import { myStorage } from '@/utils';
export default {
  name: 'recommend',
  data: function data() {
    return {
      dataList: {},
      klineDataList: {},
      mySymbolList: '',
      symbolCurrent: myStorage.get('sSymbolName')
    };
  },
  computed: {
    // 全部币对列表
    symbolAll: function symbolAll() {
      return this.$store.state.baseData.symbolAll;
    },
    symbolCurrentCoinName: function symbolCurrentCoinName() {
      return this.symbolCurrent.split('/')[0] || '';
    }
  },
  destroyed: function destroyed() {
    this.$bus.$off('RECOMMEEND_DATA');
    this.$bus.$off('RECOMMEEND_KLINE_DATA');
    this.$bus.$off('SWITCH-SYMBOL');
    this.$bus.$off('MYSYMBOL-LIST');
  },
  methods: {
    init: function init() {
      var _this = this;

      // 监听 数据
      this.$bus.$on('RECOMMEEND_DATA', function (data) {
        _this.dataList = data;
      });
      this.$bus.$on('RECOMMEEND_KLINE_DATA', function (data) {
        var keys = Object.keys(data);
        _this.klineDataList = {};
        keys.forEach(function (item) {
          if (_this.symbolCurrent === item) {
            _this.$set(_this.klineDataList, item, data[item]);
          }
        });
      });
      this.$bus.$on('SWITCH-SYMBOL', function (data) {
        _this.symbolCurrent = data;
      });
      this.$bus.$on('MYSYMBOL-LIST', function (data) {
        _this.mySymbolList = data;
      });
    },
    marketClick: function marketClick(symbol) {
      this.$bus.$emit('SWITCH-STORE', symbol);
    },
    bandLink: function bandLink(symbol) {
      var symbolArr = symbol.split('/');
      var sym = "".concat(symbolArr[0], "_").concat(symbolArr[1]);
      this.$router.push("/trade/".concat(sym));
    }
  }
};
