import mixin from 'BlockChain-ui-privatization/PC/common-mixin/innovation/view/view';
import 'BlockChain-ui-privatization/PC/common-mixin/innovation/view/view.styl';
export default {
  mixins: [mixin],
  mounted: function mounted() {
    this.init();
  },
  computed: {
    submitDisabled: function submitDisabled() {
      if (this.pageData.status === 2 || this.pageData.status === 3) {
        return false;
      }

      return true;
    },
    isLogin: function isLogin() {
      if (this.$store.state.baseData.isLogin) {
        return this.$store.state.baseData.isLogin;
      }

      return false;
    },
    // 二次确认按钮是否开启
    confirmBtnFlag: function confirmBtnFlag() {
      if (this.googleStatus === 1) {
        if (this.googleErrorFlag || this.googleVlaue.length === 0) {
          return true;
        }
      }

      if (this.isOpenMobileCheck === 1) {
        if (this.checkValue.length === 0 || this.checkErrorFlag) {
          return true;
        }
      }

      if (this.isOpenMobileCheck !== 1 && this.emailAuthOpen) {
        if (this.emailCheckValue.length === 0 || this.checkEmailErrorFlag) {
          return true;
        }
      }

      return false;
    }
  },
  methods: {
    submit: function submit() {
      if (!this.isLogin) {
        this.$router.push('/login');
        return;
      } // 兑换金额不能小于单次购买最小额度


      if (this.amount < this.pageData.singleMinRaiseMoney) {
        this.$bus.$emit('tip', {
          text: "".concat(this.$t('innov.lowerThanMinAmount')).concat(this.pageData.singleMinRaiseMoney),
          type: 'error'
        });
        return;
      } // 单笔兑换金额不能大于单次兑换最大额度


      if (this.amount > this.pageData.singleMaxRaiseMoney) {
        this.$bus.$emit('tip', {
          text: "".concat(this.$t('innov.higherThanMaxAmount')).concat(this.pageData.singleMaxRaiseMoney),
          type: 'error'
        });
        return;
      } // 接受币种余额不足


      if (this.amount > this.pageData.u_coinSymbol_amount) {
        this.$bus.$emit('tip', {
          text: "".concat(this.pageData.coinSymbol).concat(this.$t('innov.balanceNotEnough')),
          type: 'error'
        });
        return;
      } // 输入额度超出我的剩余可兑换额度


      if (this.pageData.level_amount !== null && this.amount > this.pageData.level_amount) {
        this.$bus.$emit('tip', {
          text: "".concat(this.$t('innov.surplusExchangeAmount'), ":").concat(this.pageData.level_amount),
          type: 'error'
        });
        return;
      } // 手机和邮箱必须有一个开启


      if (this.isOpenMobileCheck !== 1 && !this.emailAuthOpen) {
        this.$bus.$emit('tip', {
          text: "".concat(this.$t('innov.bindAlter')),
          type: 'error'
        });
        return;
      } // 谷歌强制认证开启的时候 判断有没有谷歌没有的话 提示去绑定


      if (this.googleStatus !== 1 && this.enforceGoogleAuth) {
        this.$bus.$emit('tip', {
          text: this.$t('assets.withdraw.bindGoogle'),
          type: 'error'
        });
        return;
      }

      if (this.verificationType === '2') {
        this.captchaObj.verify();
      } else {
        this.dialogFlag = true;
      }
    },
    // 请求数据
    getData: function getData() {
      var _this = this;

      this.axios({
        url: this.$store.state.url.common.newcoin_project_detail,
        params: {
          projectId: Number(this.pageId)
        },
        method: 'post'
      }).then(function (data) {
        if (data.code === '0') {
          var params = data.data;
          var status = params.status,
              projectStatus = params.projectStatus;

          if (status === 0 || status === 1 || status === 7) {
            _this.$router.replace('/innovation');
          }

          if (!projectStatus) {
            params.status = 0;
          } // params.email_validate_open = 0;


          _this.pageData = params;
          _this.bannerImg = params.banner;
        }
      });
    }
  }
};
