import "core-js/modules/es6.number.constructor";
import { fixInput } from '@/utils';
export default {
  name: 'page-mobilityTrade',
  props: {
    // 买卖方向
    seach: {
      type: String,
      default: ''
    },
    // 法币
    legal: {
      type: String,
      default: ''
    },
    // 数字货币
    coin: {
      type: String,
      default: ''
    }
  },
  data: function data() {
    return {
      selectCode: 1,
      inputValue: '',
      flag: true
    };
  },
  watch: {
    side: {
      immediate: true,
      handler: function handler(v) {
        var _this = this;

        this.selectCode = 0;
        this.$nextTick(function () {
          if (v === 'BUY') {
            _this.selectCode = 1;
          } else if (v === 'SELL') {
            _this.selectCode = 2;
          }
        });
      }
    },
    inputValue: function inputValue(v) {
      if (this.selectCode === 1) {
        this.inputValue = fixInput(v, this.priceFix);
      } else {
        this.inputValue = fixInput(v, this.valueFix);
      }
    }
  },
  computed: {
    buttonDisabled: function buttonDisabled() {
      var flag = true;

      if (Number(this.inputValue)) {
        flag = false;
      }

      return flag;
    },
    inputCoin: function inputCoin() {
      if (this.selectCode === 1) {
        return this.legal;
      }

      return this.coin;
    },
    title: function title() {
      if (this.side === 'BUY') {
        // 一键购买
        return this.$t('mobilityTrade.immediatelyBuy');
      }

      if (this.side === 'SELL') {
        // 一键出售
        return this.$t('mobilityTrade.immediatelySell');
      }

      return '';
    },
    side: function side() {
      if (this.seach === 'BUY') {
        return 'SELL';
      }

      if (this.seach === 'SELL') {
        return 'BUY';
      }

      return '';
    },
    market: function market() {
      return this.$store.state.baseData.market;
    },
    valueFix: function valueFix() {
      var fix = this.$store.state.baseData.defaultFiatPrecision;

      if (this.market && this.market.coinList[this.coin] && this.market.coinList[this.coin].showPrecision) {
        fix = this.market.coinList[this.coin].showPrecision;
      }

      return fix;
    },
    priceFix: function priceFix() {
      var fix = this.$store.state.baseData.defaultFiatPrecision;

      if (this.market && this.market.coinList[this.coin] && this.market.coinList[this.coin].fiatPrecision && this.market.coinList[this.coin].fiatPrecision[this.legal.toLowerCase()]) {
        fix = this.market.coinList[this.coin].fiatPrecision[this.legal.toLowerCase()];
      }

      return Number(fix);
    },
    priceText: function priceText() {
      if (this.side === 'BUY') {
        // 按金额购买
        return this.$t('mobilityTrade.priceBuy');
      }

      if (this.side === 'SELL') {
        // 按金额出售
        return this.$t('mobilityTrade.priceSell');
      }

      return '';
    },
    valueText: function valueText() {
      if (this.side === 'BUY') {
        // 按数量购买
        return this.$t('mobilityTrade.volumeBuy');
      }

      if (this.side === 'SELL') {
        // 按数量出售
        return this.$t('mobilityTrade.volumeSell');
      }

      return '';
    },
    selectList: function selectList() {
      var priceText = '';
      var valueText = '';

      if (this.side === 'BUY') {
        priceText = this.$t('mobilityTrade.priceBuy');
        valueText = this.$t('mobilityTrade.volumeBuy');
      } else {
        priceText = this.$t('mobilityTrade.priceSell');
        valueText = this.$t('mobilityTrade.volumeSell');
      }

      return [{
        code: 1,
        value: priceText
      }, {
        code: 2,
        value: valueText
      }];
    },
    inputPromptText: function inputPromptText() {
      // 请输入金额
      if (this.selectCode === 1) {
        return this.$t('mobilityTrade.addPrice');
      } // 请输入数量


      return this.$t('mobilityTrade.addVolume');
    }
  },
  methods: {
    selectChange: function selectChange(item) {
      if (this.selectCode === item.code) return;
      this.selectCode = item.code;
      this.inputValue = '';
    },
    inputChanges: function inputChanges(value) {
      this.inputValue = value;
    },
    btnClick: function btnClick() {
      this.$router.push("/mobility?side=".concat(this.side, "&legal=").concat(this.legal, "&coin=").concat(this.coin, "&tradeType=").concat(this.selectCode, "&tradeValue=").concat(this.inputValue));
    }
  }
};
