import "core-js/modules/es6.object.keys";
import "core-js/modules/es6.regexp.split";
import "core-js/modules/web.dom.iterable";
import { getCountryList } from '@/utils';
export default {
  data: function data() {
    return {
      countryKeyCode: '',
      country: '' // 所在地

    };
  },
