          this.country = this.countryMap[v].code;
        }

        if (this.defaultCountryCodeReal && this.country === '') {
          this.country = this.defaultCountryCodeReal;
        }
      }
    },
    defaultCountryCodeReal: {
      handler: function handler(v) {
        if (v && this.country === '') {
          this.country = v;
        }
      },
      immediate: true
    },
    defaultCountryCode: {
      handler: function handler(v) {
        if (v && this.countryKeyCode === '') {
          if (this.defaultCountryCodeReal) {
            this.countryKeyCode = this.countryListKeyObj[this.defaultCountryCodeReal].keyCode;
          } else {
            this.countryKeyCode = v;
          }
        }

        if (!this.defaultCountryCodeReal && !this.country && v && this.countryMap && this.countryMap[v]) {
          this.country = this.countryMap[v].code;
        }
      },
      immediate: true
    }
  },
  computed: {
    countryListKeyObj: function countryListKeyObj() {
      var list = {};
      this.countryObj.list.forEach(function (item) {
        list[item.code] = {
          code: item.code,
          keyCode: item.keyCode,
          value: item.value.split(' ')[0]
        };
      });
      return list;
    },
    // 国家渲染列表
    countryList: function countryList() {
      return this.countryObj.list;
    },
    registerCountryList: function registerCountryList() {
      var countryList = this.filterCountryList(this.$t('phoneCode'));
      return getCountryList(countryList).list;
    },
