      var arr = [];
      this.countryObj.list.forEach(function (item) {
        arr.push({
          code: item.code,
          keyCode: item.keyCode,
          value: item.value.split(' +')[0]
        });
      });
      return arr;
    },
    countryMap: function countryMap() {
      return this.countryObj.countryMap;
    },
    countryRealMap: function countryRealMap() {
      return this.countryObj.countryRealMap;
    },
    countryObj: function countryObj() {
      return this.$store.state.baseData.countryObj;
    },
    // 默认区号
    defaultCountryCode: function defaultCountryCode() {
      return this.$store.state.baseData.defaultCountryCode;
    },
    defaultCountryCodeReal: function defaultCountryCodeReal() {
      return this.$store.state.baseData.defaultCountryCodeReal;
    },
    limitCountryList: function limitCountryList() {
      return this.$store.state.baseData.limitCountryList;
    }
  },
  methods: {
    // 国家列表change
    countryChange: function countryChange(item) {
      this.country = item.code;
      this.countryKeyCode = item.keyCode;
    },
    filterCountryList: function filterCountryList(list) {
      var countryList = list;
      var limitCountryList = this.limitCountryList;

      if (limitCountryList.length > 0) {
        limitCountryList = limitCountryList.join('').split('+');
        Object.keys(countryList).forEach(function (item) {
          countryList[item] = countryList[item].filter(function (vitem) {
            var valueArr = vitem.split('+');
            return limitCountryList.indexOf(valueArr[2]) === -1;
          });
        });
      }

      return countryList;
    }
  }
};
