import { imgMap, getCookie } from '@/utils';

export default {
  name: 'appDownload',
  data() {
    return {
      flag: false,
    };
  },
  watch: {
    appDownload(v) {
      if (v) { this.flag = true; }
    },
  },
  computed: {
    navList() {
      return [
        {
          url: this.imgs.download_flag_1,
          title: this.$t('appDownLoad.flag')[0].title,
          desc: this.$t('appDownLoad.flag')[0].desc,
        },
        {
          url: this.imgs.download_flag_2,
          title: this.$t('appDownLoad.flag')[1].title,
          desc: this.$t('appDownLoad.flag')[1].desc,
        },
        {
          url: this.imgs.download_flag_3,
          title: this.$t('appDownLoad.flag')[2].title,
          desc: this.$t('appDownLoad.flag')[2].desc,
        },
        {
          url: this.imgs.download_flag_4,
          title: this.$t('appDownLoad.flag')[3].title,
          desc: this.$t('appDownLoad.flag')[3].desc,
        },
      ];
    },
    stepList() {
      const { lan } = this.$store.state.baseData;
      // let lang = lan;
      let imgList = [];
      if (lan !== 'zh_CN') {
        // lang = 'en_US';
        imgList = [
          'https://vdollarex.oss-ap-southeast-1.aliyuncs.com/upload/20210621034349552.png',
          'https://vdollarex.oss-ap-southeast-1.aliyuncs.com/upload/20210621034402461.png',
          'https://vdollarex.oss-ap-southeast-1.aliyuncs.com/upload/20210621034417528.png',
        ];
      } else {
        imgList = [
          'https://vdollarex.oss-ap-southeast-1.aliyuncs.com/upload/20210621034300825.png',
          'https://vdollarex.oss-ap-southeast-1.aliyuncs.com/upload/20210621034318694.png',
          'https://vdollarex.oss-ap-southeast-1.aliyuncs.com/upload/20210621034334715.png',
        ];
      }
      return [
        {
          url: imgList[0],
          step: this.$t('appDownLoad.setUpTestFlignt.step')[0].step,
          title: this.$t('appDownLoad.setUpTestFlignt.step')[0].title,
          desc: this.$t('appDownLoad.setUpTestFlignt.step')[0].desc,
        },
        {
          url: imgList[1],
          step: this.$t('appDownLoad.setUpTestFlignt.step')[1].step,
          title: this.$t('appDownLoad.setUpTestFlignt.step')[1].title,
          desc: this.$t('appDownLoad.setUpTestFlignt.step')[1].desc,
        },
        {
          url: imgList[2],
          step: this.$t('appDownLoad.setUpTestFlignt.step')[2].step,
          title: this.$t('appDownLoad.setUpTestFlignt.step')[2].title,
          desc: this.$t('appDownLoad.setUpTestFlignt.step')[2].desc,
        },
        // {
        //   url: this.imgs[`download_ios_04_${lang}`],
        //   step: this.$t('appDownLoad.setUp.step')[3].step,
        //   title: this.$t('appDownLoad.setUp.step')[3].title,
        //   desc: this.$t('appDownLoad.setUp.step')[3].desc,
        // },
        // {
        //   url: this.imgs[`download_ios_05_${lang}`],
        //   step: this.$t('appDownLoad.setUp.step')[4].step,
        //   title: this.$t('appDownLoad.setUp.step')[4].title,
        //   desc: this.$t('appDownLoad.setUp.step')[4].desc,
        // },
      ];
    },
    appDownload() {
      return this.$store.state.baseData.app_download;
    },
    mainBg() {
      return `background-image: url("${imgMap.appdownload_bg}#0E1A2E"`;
    },
    imgs() {
      return imgMap;
    },
  },
  methods: {
    download(type) {
      window.open(this.appDownload[`${type}_download_url`]);
    },
    init() {
      this.getchannelId();
      const footer = document.querySelector('#footer-box');
      if (footer) {
        footer.style.marginTop = 0;
      }
      if (this.appDownload) {
        this.flag = true;
      }
    },
    getchannelId() {
      const { lan } = this.$store.state.baseData;
      // 增加 取地址栏参数 转为小写逻辑
      const curDomain = document.domain.split('.').slice(-1).join() || 'co';
      // const a = window.location.search.toLocaleLowerCase();
      let channelid = getCookie('channelid') || 'vd';
      // 取地址栏参数 转为小写
      const search = window.location.search.toLocaleLowerCase().substring(1);
      const a = search.split('&');
      // eslint-disable-next-line no-plusplus
      for (let i = 0; i <= a.length - 1; i++) {
        if (a[i].indexOf('channelid=') > -1) {
          const b = a[i].split('=')[1] || channelid;
          channelid = b;
        }
      }
      // eslint-disable-next-line no-undef
      const qrcode = new QRCode(document.getElementById('qrcode'), {
        width: 100,
        height: 100,
      });

      const url = `https://m.vdollar.${curDomain}/${lan}/install?channelid=${channelid}`;
      qrcode.makeCode(url);
    },
  },
};
