export default {
  data: function data() {
    return {
      timeras: null
    };
  },
  computed: {
    // 底部 上边距
    footerClass: function footerClass() {
      var arr = [];

      if (this.routeTheme && this.routeTheme === 'homeOther') {
        arr.push('h-3-bg h-4-bd');
      } else {
        arr.push('h-1-bg h-2-bd');
        arr.push('h-2-bd');
      }

      if (this.$route.meta.footNotMrgin) {
        arr.push('no-margin');
      }

      return arr;
    },
    isLogin: function isLogin() {
      return this.$store.state.baseData.isLogin;
    },
    userInfo: function userInfo() {
      return this.$store.state.baseData.userInfo;
    },
    // 数量单位类型Number(1标的货币 2张)
    coUnitType: function coUnitType() {
      return this.$store.state.future.coUnitType;
    },
    zhang: function zhang() {
      return this.$t('futures.setFutures.text11'); // '张',
    }
  },
  watch: {
    coUnitType: function coUnitType(val) {
      if (val && val === 2) {
        this.$store.commit('SET_COUNIT_TEXT', this.zhang);
      }
    },
    zhang: function zhang(val) {
      if (val && this.coUnitType === 2) {
        this.$store.commit('SET_COUNIT_TEXT', val);
      }
    },
    userInfo: function userInfo(val) {
      if (val && this.isLogin) {
        // 获取用户配置信息
        this.$store.dispatch('getUserConfig'); // 获取仓位和资产

        this.$store.dispatch('getPositionList');
        this.getMessageCCount();
      }
    }
  },
  methods: {
    init: function init() {
      var _this = this;

      if (this.coUnitType && this.coUnitType === 2) {
        this.$store.commit('SET_COUNIT_TEXT', this.zhang);
      }

      this.$bus.$on('outUserIsLogin', function () {
        // 清除登录状态
        _this.$store.dispatch('deleteIsLogin'); // 如果当前页面需要登录 则跳转到登录


        if (_this.$route.meta.mustLogin) {
          _this.$router.push('/login');
        }
      });
      this.setStyle();
      this.timing();
      document.addEventListener('mousewheel', function () {
        _this.setTiming();

        _this.reload();
      }, false);
      document.addEventListener('click', function () {
        _this.setTiming();

        _this.reload();
      }, false);
      document.addEventListener('mousemove', function () {
        _this.setTiming();

        _this.reload();
      }, false);
      document.addEventListener('keydown', function () {
        _this.setTiming();

        _this.reload();
      }, false); // 获取获取合约列表

      this.$store.dispatch('getUserInfo'); // 获取 userinfo

      this.$store.dispatch('getFutorePublicInfo'); // 获取 public-info-v4 数据

      this.$store.dispatch('getPublicInfo'); // 获取下载数据

      this.$store.dispatch('getAppDownload'); // 获取自定义footer 和 自定义header

      this.$store.dispatch('getFooterHeander_info');
    },
    getMessageCCount: function getMessageCCount() {
      var _this2 = this;

      // 获取用户持仓和资产列表
      this.timeras = setInterval(function () {
        _this2.$store.dispatch('getMessage_count');

        if (!_this2.isLogin) {
          clearInterval(_this2.timeras);
          _this2.timeras = null;
        }
      }, 5000);
    }
  }
};
