export default {
  name: 'c-getCode',
  props: {
    className: {
      default: '',
      type: String
    },
    // 根节点class
    // : { default: 100, type: Number }, // 倒计时时间
    name: {
      default: '',
      type: String
    },
    // name标示
    buttonName: {
      default: 'codeClickButton',
      type: String
    },
    // 获取验证码按钮name
    codeFlag: {
      default: true,
      type: Boolean
    },
    // 按钮是否可点击
    autoStart: {
      default: true,
      type: Boolean
    } // 按钮按下时 自动开始倒计时

  },
  data: function data() {
    return {
      flag: true,
      // 是否可点击
      mins: 0,
      // 倒计时仅展示
      timer: null,
      code: 60
    };
  },
  watch: {
    codeFlag: function codeFlag(newValue) {
      if (newValue) {
        this.clear();
      } else {
        this.startTime();
      }
    }
  },
  created: function created() {
    var _this = this;

    this.$bus.$emit('getCode-clear');
    this.$bus.$on('getCode-clear', function (name) {
      if (name === _this.name) {
        _this.clear();
      }
    });
    this.$bus.$emit('getCode-start');
    this.$bus.$on('getCode-start', function (name) {
      if (name === _this.name) {
        _this.startTime();
      }
    });
  },
  methods: {
    codeClick: function codeClick() {
      this.$emit('click', this.name);

      if (this.autoStart) {
        this.startTime();
      }
    },
    startTime: function startTime() {
      var _this2 = this;

      this.clear();
      this.flag = false;
      this.mins = this.code;
      this.mins -= 1;
      this.timer = setInterval(function () {
        _this2.mins -= 1;

        if (_this2.mins === 0) {
          _this2.clear();
        }
      }, 1000);
    },
    clear: function clear() {
      this.$emit('clear');
      this.flag = true;
      clearInterval(this.timer);
    }
  },
  beforeDestroy: function beforeDestroy() {
    this.clear();
  }
};
