              // 止盈价格必须高于网格最高价
              this.$bus.$emit('tip', {
                text: this.$t('gridTrade.error5')
              });
              fal = false;
            } else if (Number(stopHighPrice) && Number(stopHighPrice) <= Number(this.symbolsData.close.data)) {
              this.$bus.$emit('tip', {
                text: this.$t('gridTrade.error7')
              });
              fal = false;
            } else if (Number(stopLowPrice) && Number(stopLowPrice) >= Number(lowestPrice)) {
              // 止损价格必须低于网格最低价
              this.$bus.$emit('tip', {
                text: this.$t('gridTrade.error6')
              });
              fal = false;
            } else if (Number(stopLowPrice) && Number(stopLowPrice) >= Number(this.symbolsData.close.data)) {
              this.$bus.$emit('tip', {
                text: this.$t('gridTrade.error8')
              });
              fal = false;
            }
          }

          if (this.fal && fal && flag) {
            this.fal = false;
            var submitData = {
              symbol: this.symbolCurrent,
              lowestPrice: lowestPrice,
              highestPrice: highestPrice,
              gridNumber: gridNumber,
              totalQuoteAmount: totalQuoteAmount,
              stopHighPrice: stopHighPrice,
              stopLowPrice: stopLowPrice,
              quantType: quantType,
              useOwnBase: useOwnBase,
              totalBaseAmount: totalBaseAmount,
              gridLineType: gridLineType,
              // 网格类型 1:等差 2:等比
              fee: this.fee
            };

            if (useOwnBase) {
              submitData.currentPrice = this.symbolsData.close.data;

              if (submitData.currentPrice) {
                this.gridCancelEvent(submitData);
              }
            } else {
              this.gridSubData(submitData);
            }
          }
        }
      }
