      var num = parseFloat(this.currentPage) * parseFloat(this.display);

      if (num > this.total) {
        return this.total;
      }

      return num;
    }
  },
  watch: {
    currentPage: function currentPage(val) {
      this.pages = val;
    }
  },
  methods: {
    pagination: function pagination(num) {
      if (num < 1) {
        if (parseFloat(this.currentPage) > 1 && parseFloat(this.pages) > 1) {
