export default {
  name: 'c-intoTab',
  props: {
    className: {
      default: '',
      type: String
    },
    // 根节点class
    list: {
      default: []
    },
    // [{name: xx, key: xx}]
    nowKey: {
      default: '',
      type: String
    },
    // 当前值
    disabled: {
      default: false,
      type: Boolean
    } // 是否可点击

  },
  computed: {
    // 下划线样式
    barStyle: function barStyle() {
      var _this = this;

      var area = 100 / this.list.length; // 每份占位宽度

      var ind = 0;
      this.list.forEach(function (item, i) {
        if (item.key === _this.nowKey) {
          ind = i;
        }
      });
      return {
        width: "".concat(area, "%"),
        left: "".concat(ind * area, "%")
      };
    },
    // 每一项样式
    evenStyle: function evenStyle() {
      var area = 100 / this.list.length; // 每份占位宽度

      return {
        width: "".concat(area, "%")
      };
    }
  },
  methods: {
    setNowKey: function setNowKey(item) {
      if (this.disabled) {
        return;
      }

      this.$emit('onchenges', item);
    }
  }
};
