import { directive as clickOutside } from 'v-click-outside-x';
import buttons from "../button/Button.vue";
import calendar from "./Calendar.vue";
export default {
  name: 'c-calendar',
  components: {
    calendar: calendar,
    buttons: buttons
  },
  directives: {
    clickOutside: clickOutside
  },
  props: {
    // 初始化默认 选中的值 String：直接显示，Number： 选项索引值
    value: {
      type: [String, Number],
      default: ''
    },
    options: {
      type: Array,
      default: function _default() {
        return [];
      }
    },
    name: {
      type: String,
      default: ''
    },
    // Title
    promptText: {
      type: String,
      default: ''
    },
    // 设置边框样式. 默认线性样式
    box: {
      type: Boolean,
      default: false
    },
    // 下拉框显示多少条，默认显示5条 多出显示滚动条
    optionNumber: {
      type: Number,
      default: 5
    },
    // 选项列表 行高 默认40px
    optionHeight: {
      type: Number,
      default: 40
    },
    // 下拉框右侧展开 ICON 默认 三角形icon
    appendIcon: {
      type: String,
      default: 'icon-triangle-down'
    },
    // 是否关闭
    disabled: {
      type: Boolean,
      default: false
    },
    // 是否开启头部日期详情
    showDateInfo: {
      type: Boolean,
      default: false
    },
    dateInfoTilteClass: {
      type: String,
      default: 'f-1-cl'
    },
    // 错误提示语
    errorText: {
      type: String,
      default: ''
    },
    // 将 下拉框设置成错误状态
    errorFlag: {
      type: Boolean,
      default: false
    },
    // 是否开启 清空选项的功能
    clearable: {
      type: Boolean,
      default: false
    },
    // 清空选项按钮的Icon, icon的class
    clearableIcon: {
      type: String,
      default: 'icon-clear'
    },
    // 设置输入框下划线class
    inputLineStyle: {
      type: String,
      default: ''
    },
    // 下拉框的宽度 （字符串 后面加单位 px %）
    width: {
      type: [String],
      default: ''
    },
    // 下拉框的高度
    height: {
      type: [String],
      default: ''
    },
    // 样式
    styles: {
      type: [Object, String],
      default: ''
    },
    elementId: {
      type: String,
      default: ''
    },
    // 是否需要验证
    errorHave: {
      type: Boolean,
      default: false
    },
    agoDayHide: {
      type: String,
      default: ''
    },
    futureDayHide: {
      type: String,
      default: '2554387200'
    },
    defaultShowDate: {
      type: Number
    }
  },
  data: function data() {
    return {
      // 存储选中的值 和 默认选中的值
      values: '',
      oldvalues: '',
      // 控制下拉框是都可见
      visible: false,
      // 选项列表是否有数据
      isNotOption: false,
      // isFocused
      isFocused: false,
      // 是否设置成错误状态
      isError: false,
      nowV: ''
    };
  },
  mounted: function mounted() {
    if (this.value) {
      this.values = this.value.replace(/\//g, '-');
      this.nowV = this.values;
    }
  },
  computed: {
    currentLan: function currentLan() {
      if (this.$store.state.baseData) {
        return this.$store.state.baseData.lan;
      }

      return null;
    },
    currentDate: function currentDate() {
      var date = new Date(this.value);
      return {
        year: date.getFullYear(),
        week: date.getDay(),
        month: date.getMonth(),
        day: date.getDate()
      };
    },
    previewDateText: function previewDateText() {
      if (!this.value) return '';
      var isChinese = this.currentLan === 'zh_CN';
      var weekText = this.$t("weeks.".concat(this.currentDate.week));
      var month = this.$t("months.".concat(this.currentDate.month));
      var day = "".concat(this.currentDate.day).concat(isChinese ? '日' : ''); // 2019-02-01

      var en = "".concat(weekText, ",").concat(day, " ").concat(month);
      var zh = "".concat(month).concat(day, "\uFF0C").concat(weekText);
      return isChinese ? zh : en;
    },
    classes: function classes() {
      return ['common-select', {
        'select-visible': this.visible,
        'select-disabled': this.disabled,
        'select-value': this.values,
        'select-error': this.isError
      }];
    },
    stylees: function stylees() {
      var styles = this.styles || {};
      if (this.width) styles.width = this.width;
      if (this.height) styles.width = this.height;
      return styles;
    }
  },
  watch: {
    value: function value(data) {
      if (!data) this.values = '';else if (this.value) {
        this.values = data.replace(/\//g, '-');
      }
    },
    visible: function visible(value) {
      this.$emit('opent-chang', value, this.name);
    },
    errorFlag: function errorFlag(value) {
      this.isError = value;
    }
  },
  methods: {
    clickDay: function clickDay(data) {
      this.nowV = data.replace(/\//g, '-');
      this.$emit('onChanges', data.replace(/\//g, '-'), this.name);
    },

    /* changeDate(data) {
      console.log(data); // 左右点击切换月份
    }, */
    // 下拉框 显示&隐藏
    toggleMenu: function toggleMenu() {
      this.visible = !this.visible;
    },
    // 点击 select 以外 隐藏下拉框
    onClickOutside: function onClickOutside() {
      this.oldvalues = this.values;
      this.visible = false;
    },
    // 点击 选项
    onOptionClick: function onOptionClick(item) {
      this.visible = false;
      this.isError = false;
      this.$emit('onChanges', item, this.name);
    },
    // 清除选项
    resetOptionData: function resetOptionData() {
      this.values = '';
      this.$emit('onClear', true, this.name);
    },
    // 取消选择日期
    cancelCalendar: function cancelCalendar() {
      this.$emit('onChanges', this.oldvalues, this.name);
      this.visible = false;
      this.isError = false;
    },
    confirmCalendar: function confirmCalendar() {
      this.oldvalues = this.values;
      this.visible = false;
      this.$emit('onSelect', this.nowV, this.name);
    }
  }
};
