export default {
  methods: {
    init: function init() {
      var _this = this;

      this.goM();
      this.$bus.$on('outUserIsLogin', function () {
        // 清除登录状态
        _this.$store.dispatch('deleteIsLogin'); // 如果当前页面需要登录 则跳转到登录


        if (_this.$route.meta.mustLogin) {
          _this.$router.push('/login');
        }
      });
      this.setStyle();
      this.timing();
      document.addEventListener('mousewheel', function () {
        _this.setTiming();

        _this.reload();
      }, false);
      document.addEventListener('click', function () {
        _this.setTiming();

        _this.reload();
      }, false);
      document.addEventListener('mousemove', function () {
        _this.setTiming();

        _this.reload();
      }, false);
      document.addEventListener('keydown', function () {
        _this.setTiming();

        _this.reload();
      }, false); // 获取 public-info 数据

      this.$store.dispatch('getPublicInfo');
      this.$store.dispatch('getAppDownload'); // 获取 userinfo

      this.$store.dispatch('getUserInfo'); // 获取自定义footer 和 自定义header

      this.$store.dispatch('getFooterHeander_info');

      if (this.loginFlag === '2') {
        clearInterval(this.timer);
        this.$store.dispatch('getMessage_count');
        this.timer = setInterval(function () {
          _this.$store.dispatch('getMessage_count');
        }, 15000);
      } else {
        clearInterval(this.timer);
      }
    }
  }
};
