import mixin from 'blockchain-ui-privatization/PC/common-mixin/modules/trade/depath/index';
import 'blockchain-ui-privatization/PC/common-mixin/modules/trade/depath/index/tradeIndex.styl';
import { myStorage } from 'blockchain-ui-privatization/utils';
import tableList from '@/components/modules/trade/tableList/index.vue'; // 实时成交

import newTrade from '@/components/modules/trade/newTrade/index.vue';
export default {
  mixins: [mixin],
  components: {
    tableList: tableList,
    newTrade: newTrade
  },
  mounted: function mounted() {
    this.init();
  },
  props: {
    showGridFlag: {
      default: false,
      type: Boolean
    }
  },
  data: function data() {
    return {
      minHeight: 352,
      // 卖盘 高度
      sellHeight: 224,
      // 买盘 高度
      buyHeight: 224,
      maxHeeight: 600
    };
  },
  watch: {
    showGridFlag: function showGridFlag(v) {
      if (v) {
        this.buyHeight = 231;
        this.sellHeight = 231;
        this.sellLineNumber = 22;
        this.buyLineNumber = 22;
      } else {
        this.buyHeight = 224;
        this.sellHeight = 224;
        this.sellLineNumber = 22;
        this.buyLineNumber = 22;
      }
    }
  },
  methods: {
    init: function init() {
      var _this = this;

      if (this.moduleType === 'lever') {
        // 卖盘 高度
        this.sellHeight = 416; // 买盘 高度

        this.buyHeight = 416; // 显示条数

        this.sellLineNumber = 26;
        this.buyLineNumber = 26;
        this.maxHeeight = 840; // 当前选中的货币对

        this.symbolCurrent = myStorage.get('leverSymbolName');
      }

      this.setBoxHeight(); // 监听 深度数据

      this.$bus.$on('DEPTH_DATA', function (data) {
        if (data && _this.symbolCurrent === data.symbol) {
          _this.dataList = JSON.parse(JSON.stringify(data));
        } else {
          _this.dataList = {
            asksData: [],
            buyData: [],
            depthMaxNumber: null
          };
        }
      }); // 监听 市场（最新价格） 数据

      this.$bus.$on('MARKET_DATA', function (data) {
        _this.marketData = JSON.parse(JSON.stringify(data));
      }); // 获取 当前选中的货币对

      this.$bus.$on('SYMBOL_CURRENT', function (val) {
        _this.dataList = {
          asksData: [],
          buyData: [],
          depthMaxNumber: null
        };
        _this.symbolCurrent = val;
        _this.currentdepthClass = 0;
      }); // 监听 浏览器窗口大小改变

      this.$bus.$on('WINFOW_ON_RESIIZE', function () {
        // 设置国际版TV的高度
        if (document.body.clientWidth < 961) {
          _this.sellHeight = 100;
          _this.buyHeight = 100;
          _this.sellLineNumber = 5;
          _this.buyLineNumber = 5;
        } else if (_this.moduleType === 'lever') {
          _this.sellHeight = 416;
          _this.buyHeight = 416;
          _this.sellLineNumber = 26;
          _this.buyLineNumber = 26;
        } else {
          _this.buyHeight = 224;

          if (_this.showGridFlag) {
            _this.buyHeight = 418;
          }

          _this.sellHeight = 224;
          _this.sellLineNumber = 24;
          _this.buyLineNumber = 24;
        }

        _this.setBoxHeight(false);
      });
    }
  }
};
