export default {
  name: 'c-iconButton',
  data: function data() {
    return {
      isHover: false,
      isClick: false
    };
  },
  props: {
    havebg: {
      default: false,
      type: Boolean
    }
  },
  computed: {
    bgClass: function bgClass() {
      var str = '';

      if (this.isHover || this.havebg) {
        str = 'iconButton-hover';
      }

      if (this.isClick) {
        str = 'iconButton-active';
      }

      return str;
    }
  },
  methods: {
    handClick: function handClick() {
      this.$emit('click');
    },
    // input 鼠标划入
    handMouseenter: function handMouseenter() {
      this.isHover = true;
    },
    // input 鼠标划出
    handMouseleave: function handMouseleave() {
      this.isHover = false;
    },
    handMouseDown: function handMouseDown() {
      this.isClick = true;
    },
    handMouseUp: function handMouseUp() {
      this.isClick = false;
    }
  }
};
