export default {
  name: 'c-switch',
  data: function data() {
    return {
      isChecked: this.value
    };
  },
  props: {
    width: {
      default: '30',
      type: String
    },
    // 该容器 width属性
    height: {
      default: '12',
      type: String
    },
    // 该容器 height属性
    // 开关上圆的宽度，默认14px
    circleWidth: {
      default: '18',
      type: String
    },
    // 开关上圆的高度，默认14px
    circleHeight: {
      default: '18',
      type: String
    },
    // 控制开关开启关闭
    value: {
      default: true,
      type: Boolean
    }
  },
  computed: {
    boxStyle: function boxStyle() {
      return {
        width: "".concat(this.width, "px"),
        height: "".concat(this.height, "px"),
        'border-radius': "".concat(this.height, "px")
      };
    },
    circleStyle: function circleStyle() {
      return {
        width: "".concat(this.circleWidth, "px"),
        height: "".concat(this.circleHeight, "px"),
        'border-radius': '50%',
        top: "".concat((this.height - this.circleHeight) / 2, "px"),
        left: '0px'
      };
    }
  },
  watch: {
    value: function value(val) {
      this.isChecked = val;
    },
    isChecked: function isChecked(val) {
      this.$emit('onchange', val);
    }
  },
  methods: {
    toggle: function toggle() {
      this.$emit('click');
    }
  }
};
