// 错误信息占位容器
.common-AliyunCaptcha-errorStence {
    height: 24px;
    position: relative;
    // 错误文案容器
    .common-AliyunCaptcha-error {
      position: absolute;
      bottom: 2px;
      margin: 0;
      font-size: 12px;
      user-select:none;
    }
  }
.common-AliyunCaptcha {
  // 弹窗
  .nc-container .nc_scale .imgCaptcha, .nc-container .nc_scale .clickCaptcha {
    border: none;
  }
  .nc-container .clickCaptcha_text .btn_refresh {
    background: none
  }
  .nc-container .nc_scale .clickCaptcha div {
    height: 40px;
    line-height: 40px;
  }
  .nc-container .clickCaptcha_text .icon_close {
    line-height: 40px;
  }
  .clickCaptcha {
    width: 380px!important;
    top: 45px!important;
    box-shadow: 0px 2px 10px rgba(0,0,0,0.30)!important;
    padding-bottom: 30px!important;
    img {
      width: 300px!important;
    }
    .clickCaptcha_img {
      text-align: center!important;
    }
  }
  .imgCaptcha {
    width: 380px!important;
    min-height: 200px!important;
    box-sizing: border-box!important;
    border: none!important;
    top: 44px!important;
    padding: 40px!important;
    box-shadow: 0px 2px 10px rgba(0,0,0,0.30)!important;
    // input
    .imgCaptcha_text {
      margin: 0!important;
      height: 40px!important;
      width: 200px!important;
      box-sizing: border-box!important;
      overflow: hidden!important;
      border-bottom-width: 2px!important;
      border-bottom-style: solid!important;
      input {
        box-sizing: border-box!important;
        width: 100%!important;
        height: 100%!important;
        margin: 0!important;
      }
    }
    // 图
    .imgCaptcha_img {
      margin: 0!important;
      width: 100px!important;
      height: 40px!important;
      img {
        width: 100px!important;
        height: 40px!important;
        display: block!important;
      }
    }
    .btn_refresh {
      background: none;
    }
    // button
    .imgCaptcha_btn {
      background: none!important;
      width: 100%!important;
      margin: 40px 0 0 0!important;
      padding: 0!important;
      // 错误
      .nc_captcha_img_text {
        background: none;
      }
      .nc_scale_submit {
        width: 100%;
        height: 40px;
        line-height: 40px;
        border-radius: 2px;
        font-size: 14px;
      }
    }
  }
  .ln {
    padding: 0;
    .nc_wrapper {
      width: 100% !important;
    }
    // 滑条背景  nc_bg滑动后一系列状态背景 scale_text2为未滑动
    .nc-container .nc_scale .nc_bg
    .nc-container .nc_scale .scale_text2 {
      border-radius: 2px;
    }
    // 滑块滑动后 ok状态
    .nc-container .nc_scale .btn_ok {
      line-height: 40px;
      top: -1px;
      width: 56px!important;
      height: 40px;
      line-height: 40px;
      border: none;
      border-radius: 2px;
    }
    .nc-container .nc_scale .scale_text {
      line-height: 40px;
    }
    .nc-container .clickCaptcha .clickCaptcha_img img {
      margin: 0;
    }
    // 初始
    .nc_scale {
      height: 40px;
      line-height: 40px;
      border-radius: 2px;
      // 划块
      .btn_slide {
        line-height: 40px;
        top: -1px;
        width: 56px;
        height: 40px;
        line-height: 40px;
        border: none;
        border-radius: 2px;
      }
    }
  }
}
