import mixin from 'BlockChain-ui-privatization/PC/common-mixin/modules/trade/depath/index';
import 'BlockChain-ui-privatization/PC/common-mixin/modules/trade/depath/index/tradeIndex.styl';
import { myStorage } from 'BlockChain-ui-privatization/utils';
import tableList from '@/components/modules/trade/tableList/index.vue';
// 实时成交
import newTrade from '@/components/modules/trade/newTrade/index.vue';

export default {
  mixins: [mixin],
  components: {
    tableList,
    newTrade,
  },
  mounted() {
    this.init();
  },
  props: {
    showGridFlag: {
      default: false,
      type: Boolean,
    },
  },
  data() {
    return {
      minHeight: 352,
      // 卖盘 高度
      sellHeight: 224,
      // 买盘 高度
      buyHeight: 224,
      maxHeeight: 600,
    };
  },
  watch: {
    showGridFlag(v) {
      if (v) {
        this.buyHeight = 231;
        this.sellHeight = 231;
        this.sellLineNumber = 22;
        this.buyLineNumber = 22;
      } else {
        this.buyHeight = 224;
        this.sellHeight = 224;
        this.sellLineNumber = 22;
        this.buyLineNumber = 22;
      }
    },
  },
  methods: {
    init() {
      if (this.moduleType === 'lever') {
        // 卖盘 高度
        this.sellHeight = 416;
        // 买盘 高度
        this.buyHeight = 416;
        // 显示条数
        this.sellLineNumber = 26;
        this.buyLineNumber = 26;
        this.maxHeeight = 840;
        // 当前选中的货币对
        this.symbolCurrent = myStorage.get('leverSymbolName');
      }

      this.setBoxHeight();
      // 监听 深度数据
      this.$bus.$on('DEPTH_DATA', (data) => {
        if (data && this.symbolCurrent === data.symbol) {
          this.dataList = JSON.parse(JSON.stringify(data));
        } else {
          this.dataList = {
            asksData: [],
            buyData: [],
            depthMaxNumber: null,
          };
        }
      });
      // 监听 市场（最新价格） 数据
      this.$bus.$on('MARKET_DATA', (data) => {
        this.marketData = JSON.parse(JSON.stringify(data));
      });
      // 获取 当前选中的货币对
      this.$bus.$on('SYMBOL_CURRENT', (val) => {
        this.dataList = {
          asksData: [],
          buyData: [],
          depthMaxNumber: null,
        };
        this.symbolCurrent = val;
        this.currentdepthClass = 0;
      });
      // 监听 浏览器窗口大小改变
      this.$bus.$on('WINFOW_ON_RESIIZE', () => {
        // 设置国际版TV的高度
        if (document.body.clientWidth < 961) {
          this.sellHeight = 100;
          this.buyHeight = 100;
          this.sellLineNumber = 5;
          this.buyLineNumber = 5;
        } else if (this.moduleType === 'lever') {
          this.sellHeight = 416;
          this.buyHeight = 416;
          this.sellLineNumber = 26;
          this.buyLineNumber = 26;
        } else {
          this.buyHeight = 224;
          if (this.showGridFlag) {
            this.buyHeight = 418;
          }
          this.sellHeight = 224;
          this.sellLineNumber = 24;
          this.buyLineNumber = 24;
        }
        this.setBoxHeight(false);
      });
    },
  },
};
