export default {
  name: 'c-alert',
  props: {
    imgMap: {
      type: Object,
      default: () => {},
      required: true,
    },
    // 展示变量
    showFlag: { default: false, type: Boolean },
    // button文案
    buttonText: { default: '', type: String },
    haveClose: { default: true, type: Boolean },
    // img
    imageType: {
      default: '2',
      type: String,
    },
  },
  computed: {
    imgClass() {
      const { imgMap } = this;
      const img = this.imageType === '1' ? imgMap.alert : imgMap.alert2;
      return {
        backgroundImage: `url(${img})`,
      };
    },
    buttonTextProp() {
      if (this.buttonText.length) {
        return this.buttonText;
      }
      // 我知道了
      return this.$t('components.alert.buttonText');
    },
  },
  methods: {
    close() {
      this.$emit('close');
    },
    confirm() {
      this.$emit('confirm');
    },
  },
};
