export default {
  name: 'c-iconButton',
  data() {
    return {
      isHover: false,
      isClick: false,
    };
  },
  props: {
    havebg: { default: false, type: Boolean },
  },
  computed: {
    bgClass() {
      let str = '';
      if (this.isHover || this.havebg) {
        str = 'iconButton-hover';
      }
      if (this.isClick) {
        str = 'iconButton-active';
      }
      return str;
    },
  },
  methods: {
    handClick() {
      this.$emit('click');
    },
    // input 鼠标划入
    handMouseenter() {
      this.isHover = true;
    },
    // input 鼠标划出
    handMouseleave() {
      this.isHover = false;
    },
    handMouseDown() {
      this.isClick = true;
    },
    handMouseUp() {
      this.isClick = false;
    },
  },
};
