export default {
  name: 'c-tip',
  data() {
    return {
      list: [
        // { text: 'error', time: 3000, type: 'error' },
        // { text: 'success', time: 3000, type: 'success' },
        // { text: 'info', time: 3000, type: 'info' },
        // { text: 'warning', time: 3000, type: 'warning' }
      ],
    };
  },
  computed: {
    domList() {
      const list = [];
      this.list.forEach((item) => {
        let color = '';
        switch (item.type) {
          case 'error':
            color = 'a-19-bd';
            break;
          case 'success':
            color = 'a-18-bd';
            break;
          case 'info':
            color = 'a-12-bd';
            break;
          case 'warning':
            color = 'a-20-bd';
            break;
          default:
            color = 'a-12-bd';
        }
        list.push({ ...item, ...{ color } });
      });
      return list;
    },
  },
  mounted() {
    this.$bus.$on('tip', ({ text, type = 'info' }) => {
      const time = new Date().getTime();
      this.list.unshift({
        text,
        type,
        time,
      });
      setTimeout(() => {
        let id = -1;
        this.list.forEach((item, index) => {
          if (item.time === time) id = index;
        });
        this.list.splice(id, 1);
      }, 3000);
    });
  },
};
