export default {
  name: 'c-date-picker',
  props: {
    value: {
      type: Date,
      default: () => new Date(),
    },
  },
  data() {
    const { year, month } = this.getYearMonthDate(this.value);
    return {
      isVisable: false, // 控制面板是否可见
      time: {
        year,
        month,
      },
    };
  },
  directives: {
    clickOutSide: { // 指令的生命周期
      bind(el, bindings, vnode) {
        // 把事件绑定在document上
        const handler = (e) => {
          if (el.contains(e.target)) {
            // 判断一下是否当前面板已经显示出来了
            if (!vnode.context.isVisable) {
              vnode.context.focus();
            }
          } else if (vnode.context.isVisable) {
            vnode.context.blur();
          }
        };
        // eslint-disable-next-line no-param-reassign
        el.handler = handler;
        document.addEventListener('click', handler);
      },
      unbind(el) {
        document.removeEventListener('click', el.handler);
      },
    },
  },
  computed: {
    weekDays() {
      return this.$t('common.date');
    },
    formatDate() {
      const { year, month, day } = this.getYearMonthDate(this.value);
      return `${year}-${month + 1}-${day}`;
    },
    visabledDays() {
      // 获取当前是周几
      const { year, month } = this.getYearMonthDate(
        this.getDate(this.time.year, this.time.month, 1),
      );
      // 获取当前月份第一天
      const currentFirstDay = this.getDate(year, month, 1);
      // 获取当前是周几 把天数往前移动几天
      const week = currentFirstDay.getDay();
      // 当前开始的天数
      const startDay = currentFirstDay - week * 60 * 60 * 1000 * 24;
      // 循环42天
      const arr = [];
      // eslint-disable-next-line no-plusplus
      for (let i = 0; i < 42; i++) {
        // 依次循环出42天
        arr.push(new Date(startDay + i * 60 * 60 * 1000 * 24));
      }
      return arr;
    },
  },
  methods: {
    // 获取年月日
    getYearMonthDate(date) {
      const year = date.getFullYear();
      const month = date.getMonth();
      const day = date.getDate();
      return { year, month, day };
    },
    // 日期化
    getDate(year, month, day) {
      return new Date(year, month, day);
    },
    focus() {
      this.isVisable = true;
    },
    blur() {
      this.isVisable = false;
    },
    isCurrentMonth(date) {
      // 是否为当月日期
      // eslint-disable-next-line max-len
      const { year, month } = this.getYearMonthDate(this.getDate(this.time.year, this.time.month, 1));
      const { year: y, month: m } = this.getYearMonthDate(date);
      return year === y && month === m;
    },
    isToday(date) {
      // 是否为当月日期
      const { year, month, day } = this.getYearMonthDate(new Date());
      const { year: y, month: m, day: d } = this.getYearMonthDate(date);
      return year === y && month === m && day === d;
    },
    isSelect(date) {
      // 获取当前的年月日
      const { year, month, day } = this.getYearMonthDate(this.value);
      const { year: y, month: m, day: d } = this.getYearMonthDate(date);
      return year === y && month === m && day === d;
    },
    prevMonth() {
      // 获取当前月的一个日期
      const d = this.getDate(this.time.year, this.time.month, 1);
      d.setMonth(d.getMonth() - 1);
      this.time = this.getYearMonthDate(d);
    },
    nextMonth() {
      // 获取当前月的一个日期
      const d = this.getDate(this.time.year, this.time.month, 1);
      d.setMonth(d.getMonth() + 1);
      this.time = this.getYearMonthDate(d);
    },
    // 选择日期
    onChangeDate(date) {
      this.time = this.getYearMonthDate(date);
      // eslint-disable-next-line no-console
      // console.log(date, 'data------->');
      this.$emit('input', date);
      this.blur(); // 关闭面板
    },
  },
};

