// 根容器
.common-geetest {
  // 基础占位容器 (默认)
  .common-geetest-baseStance {
    position: relative;
    height: 38px;
    border-bottom-width: 2px;
    border-bottom-style: solid;
    // 下划线动效容器
    .common-geetest-activeLine {
      position: absolute;
      top: 38px;
      height: 2px;
      width: 0%;
      left: 50%;
      transform: translateX(-50%);
      display: inline-block;
      -webkit-transition: 0.5s;
      transition: 0.5s;
      animation-delay: 0.5s
    }
    // loading容器
    .common-geetest-loading {
      position: absolute;
      height: 30px;
      line-height: 30px;
      bottom: 0;
      left: 0;
      width: 100%;
      font-size: 14px
    }
  }
  // 基础占位容器 (错误时)
  .common-geetest-baseStance-error {
  }
  // 错误信息占位容器
  .common-geetest-errorStence {
    height: 24px;
    position: relative;
    // 错误文案容器
    .common-geetest-error {
      position: absolute;
      bottom: 2px;
      margin: 0;
      font-size: 12px;
      user-select:none;
    }
  }
}

// 极验样式部分
#captchaBox {
  // 极验 自身最外层盒子
  .geetest_wind {
    height: 38px;
  }
  // 初始状态 小icon部分
  .geetest_radar {
    width: 24px;
    height: 24px;
    top: 3px;
  }
  // 初始状态 重试状态 文字部分
  .geetest_radar_tip_content, geetest_reset_tip_content {
    font-size: 14px;
  }
  .geetest_reset_tip_content
  // 文字部分盒子
  .geetest_radar_tip {
    height: 38px;
    line-height: 42px;
  }
  // 等待状态
  .geetest_holder.geetest_wind.geetest_radar_click_ready .geetest_radar_tip {
    opacity: 1
  }
  // loading
  .geetest_radar_btn {
  }
  .geetest_holder.geetest_wind .geetest_radar_btn ,
  .geetest_holder.geetest_wind .geetest_radar_btn:hover{
    background: none;
    border: none;
  }
  // 成功后
  .geetest_holder.geetest_wind .geetest_success_btn,
  .geetest_holder.geetest_wind .geetest_success_btn:hover {
    border: none;
    height: 38px;
    background-color transparent;
  }
  .geetest_holder.geetest_wind .geetest_success_btn .geetest_success_box .geetest_success_show {
    background: none;
    border: none;
  }
  .geetest_logo,
  .geetest_success_logo {
    display: none;
  }
}
