export default {
  name: 'c-navMenu',
  data() {
    return {
      hoverId: '',
    };
  },
  props: {
    // 组件整体宽度
    width: {
      default: '190',
      type: [Number, String],
    },
    // 元素行高
    lineHidth1: {
      default: '56',
      type: [Number, String],
    },
    // 元素行高
    subLineHidth: {
      default: '56',
      type: [Number, String],
    },
    // 菜单列表
    navList: {
      default: () => [],
      type: Array,
    },
    // 默认选中第几个
    navListActive: {
      default: 1,
      type: [Number, String],
    },
  },
  computed: {
    // 组件整体宽度
    boxStyle() {
      return {
        width: `${this.width}px`,
      };
    },
    topStyle() {
      return {
        top: `${(this.lineHidth1 - 18) / 2}px`,
      };
    },
  },
  methods: {
    lineHeightStyle1(item) {
      if (item.type === 1) {
        return {
          'line-height': `${this.lineHidth1}px`,
        };
      }
      return {
        'line-height': `${this.subLineHidth}px`,
      };
    },
    heightStyle(item) {
      if (item.type === 1) {
        return {
          'line-height': `${this.lineHidth1}px`,
        };
      }
      return {
        'line-height': `${this.subLineHidth}px`,
      };
    },
    // 菜单栏点击事件
    listTextChange(item) {
      this.$emit('listChange', item);
    },
    handMouseenter(item) {
      this.hoverId = item.id;
    },
    handMouseleave() {
      this.hoverId = '';
    },
    returnClass(item) {
      let str = '';
      if (this.navListActive === item.id) {
        str = 'listActive a-4-bg b-1-cl';
      } else if (this.hoverId === item.id) {
        str = 'listActive b-1-cl a-4-bg';
      } else {
        str = 'nolistActive a-5-bg b-2-cl';
      }
      if (item.type === 1) {
        str = `${str} medium`;
      }
      if (item.type === 2) {
        str = `${str} min-medium`;
      }
      return str;
    },
  },
};
