.common-select {
  position: relative;
  cursor: pointer;
// 展示容器
  .input_line_content {
    height: 46px;
    box-sizing: border-box;
    border-bottom-style solid
    border-bottom-width 2px
    position: relative;
  }
  // 输入框容器
  .input_line_inp {
    position: absolute;
    bottom: 0px;
    z-index: 2;
    width: 100%;
    border: 0px;
    font-size: 14px;
    height: 30px;
    background: none;
    box-sizing: border-box;
    outline: none;
    display: inline-block;
    -webkit-transition: 0.3s;
    transition: 0.3s;
    cursor: pointer;
  }
  // 提示文案容器
  .input_line_prompt {
    z-index: 1;
    position: absolute;
    height: 30px;
    line-height: 30px;
    bottom: 0px;
    left: 0;
    font-size: 14px;
    -webkit-transition: 0.3s;
    transition: 0.3s;
    user-select:none;
  }
  // 下划线动效容器
  .input_line_activeLine {
    position: absolute;
    z-index: 3;
    bottom: -2px;
    height: 2px;
    width: 0;
    left: 50%;
    transform: translateX(-50%);
    display: inline-block;
    -transition: 0.5s;
    -webkit-transition: 0.5s;
    -ms-transition: 0.5s;
    animation-delay: 0.5s
    -webkit-animation-delay: 0.5s
    -ms-animation-delay: 0.5s
  }
  // 错误文案容器
  .input_line_errorStence {
    height: 24px;
    position: relative;
    // 错误文案容器
    .input_line_error {
      position: absolute;
      bottom: 0;
      margin: 0;
      font-size: 12px;
      user-select:none;
    }
  }
  // ICON
  .icon-triangle {
    // color: #828EA1;
    position: absolute;
    right: 5px;
    bottom:12px;
    width:0;
    height:0;
    border-width:4px 4px 0;
    border-style:solid;
    border-left-color: transparent!important;
    border-right-color: transparent!important;
 //   border-color:#828EA1 transparent transparent;/*灰 透明 透明 */
    // text-align: center;
    // width: 20px;
    // line-height: 10px;
    // cursor: pointer;
    transition: all .3s ease-in-out;
  }
  // 清除 ICON
  .icon-clear-btn {
    position: absolute;
    right: 0;
    bottom:0
    text-align: center;
    width: 20px;
    line-height: 30px;
    cursor: pointer;
    display: none;
  }
  // 下拉框
  .select-options-box {
    position: absolute;
    transform-origin: center top 0px;
    top: 46px;
    left: 0;
    right: 0;
    z-index: 9;
    box-shadow: 0 3px 4px 1px rgba(0,0,0,0.28);
    border-radius: 2px;
    font-size: 14px;
    padding: 10px 0;
    .select-option-list {
      .select-option-item {
          line-height: 40px;
          font-size: 12px;
          height: 40px;
          padding: 0 20px;
          cursor: pointer;
      }
      .select-option-item-double {
        padding: 8px 20px;
        line-height: 20px;
        cursor: pointer;
      }
      .select-option-item-img{
        line-height: 40px;
          font-size: 12px;
          height: 40px;
          padding: 0 20px;
          cursor: pointer;
        .imgBox{
          width:40px;
          height:40px;
          float:left;
          margin-right :10px;
          img{
            width:30px;
            height:30px;
            border-radius:50%;
            margin-top:5px;
          }
        }
      }
    }
    // 没有选项的样式
    .not_option {
      font-size: 12px;
      line-height: 40px;
      padding 0 20px;
    }
  }
  // input聚焦/具有文本时 提示文案浮起吃
  .input_line_inp:valid+.input_line_prompt,
  &.select-value .input_line_prompt,
  &.select-visible .input_line_prompt  {
      font-size: 12px;
      -ms-transform: translateY(-20px);
      -webkit-transform: translateY(-20px);
      transform: translateY(-20px);
  }
  // input聚焦 下划线动效
  .input_line_content:hover .input_line_activeLine,
  &.select-visible .input_line_activeLine,
  .input_line_inp:valid+.input_line_prompt{
      width: 100%;
  }
  // 错误样式
  &.select-error {
    .input_line_activeLine {
      width: 100%;
      background: #EB4D5C;
    }
    .input_line_prompt {
      color: #EB4D5C;
      animation: shake 0.6s cubic-bezier(0.25, 0.8, 0.5, 1);
    }
  }
  // 展开样式
  &.select-visible {
    // 三角旋转180度
    .icon-triangle-down {
      transform: rotate(180deg);
    }
  }
  // 有值样式
  &:hover {
    // 清除按钮显示
    .icon-clear-btn {
      z-index: 9;
      background-color:#000;
      display: inline-block;
    }
  }
  // 展开动画
  .slideInDown-enter-active {
  transition: all .3s ease;
  }
  .slideInDown-leave-active {
    transition: all .3s cubic-bezier(1.0, 0.5, 0.8, 1.0);
  }
  .slideInDown-enter, .slideInDown-leave-to {
    transform: translateY(-15px);
    opacity: 0;
  }
  // 错误 label 抖动 动画
  @-webkit-keyframes shake {
    59% {
      margin-left: 0;
    }
    60%, 80% {
      margin-left: 2px;
    }
    70%, 90% {
      margin-left: -2px;
    }
  }
  @keyframes shake {
    59% {
      margin-left: 0;
    }
    60%, 80% {
      margin-left: 2px;
    }
    70%, 90% {
      margin-left: -2px;
    }
  }
}
