.area-line-content {
  .common-textarea {
    resize:none;
    line-height: 30px;
    width: 380px;
    max-height: 150px;
    padding: 0;
    margin: 0;
  }
  // 基础占位容器 (默认)
  .input-line-baseStance {
    // height: 30px;
    padding-top: 14px;
    // box-sizing: border-box;
    border-bottom-width: 2px;
    border-bottom-style: solid;
    position: relative;
    .area-icon {
      position: absolute;
      right: 0;
      bottom: 0;
    }
    // 输入框
    .input_line_inp {
      // position: absolute;
      bottom: 0px;
      // z-index: 2;
      width: 100%;
      border: 0px;
      font-size: 14px;
      // height: 30px;
      background: none;
      max-height: 150px;
      // box-sizing: border-box;
      // outline: none;
      textarea {
        // height: 30px;
        width: 100%;
        font-size: 14px;
        position: absolute;
        bottom: 0;
      }
      textarea::-webkit-scrollbar {
        display: none;
      }
      .input-line-slot {
        float: left;
        display: inline-block;
        position: absolute;
        height: auto;
        right: 0;
        bottom: 0;
      }
    }
    // 提示文案容器
    .input-line-prompt {
      cursor: text;
      z-index: 1;
      position: absolute;
      height: 30px;
      line-height: 30px;
      top: 14px;
      left: 0;
      font-size: 14px;
      -webkit-transition: 0.3s;
      transition: 0.3s;
      user-select:none;
    }
    // input聚焦/具有文本 时 提示文案浮起
    .input-line-prompt-active {
      cursor: auto;
      font-size: 12px;
      -ms-transform: translateY(-20px);
      -webkit-transform: translateY(-20px);
      transform: translateY(-20px);
    }
    // 下划线动效容器
    .input-line-activeLine {
      position: absolute;
      z-index: 3;
      // top: 43px;
      bottom: -2px;
      height: 2px;
      width: 0;
      left: 50%;
      transform: translateX(-50%);
      display: inline-block;
      transition: 0.5s;
      -webkit-transition: 0.5s;
      -ms-transition: 0.5s;
      animation-delay: 0.5s
      -webkit-animation-delay: 0.5s
      -ms-animation-delay: 0.5s
    }
  }
  // 基础占位容器 (禁用时)
  .input-line-baseStance-disabled {
    border-bottom-width: 2px;
    border-bottom-style: dotted;
    .input-line-prompt-active {
      cursor: no-drop;
    }
    textarea {
      cursor: no-drop;
    }
  }
  // 基础占位容器 (错误时)
  .input-line-baseStance-error {
    // 提示文案容器 (错误时)
    .input-line-prompt {
      animation: shake 0.6s cubic-bezier(0.25, 0.8, 0.5, 1);
    }
  }
  // 错误信息占位容器
  .input-line-errorStence {
    height: 24px;
    position: relative;
    // 错误文案容器
    .input-line-error {
      position: absolute;
      top: 2px;
      margin: 0;
      font-size: 12px;
      user-select:none;
    }
    // 警示框
    .input-line-warning {
      position: absolute;
      bottom: 5px;
      font-size: 12px;
    }
  }

  .input_line_inp:focus+.input-line-prompt,
  .input_line_inp:valid+.input-line-prompt {
  }
  // 错误 label 抖动 动画
  @-webkit-keyframes shake {
    59% {
      margin-left: 0;
    }
    60%, 80% {
      margin-left: 2px;
    }
    70%, 90% {
      margin-left: -2px;
    }
  }
  @keyframes shake {
    59% {
      margin-left: 0;
    }
    60%, 80% {
      margin-left: 2px;
    }
    70%, 90% {
      margin-left: -2px;
    }
  }
}
