import VueScript2 from 'vue-script2';
import bowser from 'bowser';

export default {
  name: 'common-AliyunCaptcha',
  props: {
    width: { default: '100%', type: String }, // 该容器根容器 width属性
    marginTop: { default: '0px', type: String }, // 该组件根容器 margin-top属性
    errorHave: { default: false, type: Boolean }, // 是否有错误文案
    errorText: { default: '', type: String }, // 错误文案
    errorFlag: { default: false, type: Boolean }, // 错误文案是否显示
  },
  computed: {
    appkey() {
      return this.$store.state.baseData.publicInfo.nc.nc_appkey;
      // return 'FFFF0N000000000066A8';
      // return 'FFFF0000000001780E11';
    },
    prefix() {
      return this.$store.state.baseData.publicInfo.nc.nc_prefix;
    },
    dynamicId() {
      const ncId = `a${this.randomCode(10)}`;
      return ncId;
    },
    // 根容器 行内样式
    contentStyle() {
      return {
        width: this.width,
        marginTop: this.marginTop,
      };
    },
    lan() {
      return this.$store.state.baseData.lan;
    },
    alyLan() {
      let language = '';
      // 简体中文
      if (this.lan === 'zh_CN') {
        language = 'cn';
        // 繁体中文
      } else if (this.lan === 'el_GR') {
        language = 'tw';
        // 葡萄牙
      } else if (this.lan === 'en_US') {
        language = 'en';
      } else {
        const arr = [
          'ar_SA', // 阿拉伯文
          'de_DE', // 德文
          'es_ES', // 西班牙文
          'fr_FR', // 法文
          'in_ID', // 印尼语
          'it_IT', // 意大利文
          'iw_HE', // 希伯来语
          'ja_JP', // 日文
          'ko_KR', // 韓文
          'nl_NL', // 荷蘭文
          'pt_BR', // 波蘭文
          'ru_RU', // 俄文
          'th_TH', // 泰文
          'tr_TR', // 土耳其文
          'vi_VN', // 越南文
        ];
        if (arr.indexOf(this.lan) !== -1) {
          language = this.lan;
        }
      }
      if (!language.length) {
        language = 'en';
      }
      return language;
    },
  },
  mounted() {
    this.init();
  },
  methods: {
    randomCode(length) {
      const arr = [];
      let str = '';
      arr.length = 10;
      arr.forEach(() => {
        str += Math.random()
          .toString(36)
          .substr(2);
      });
      return str.substr(0, length);
    },
    init() {
      let nc = null;
      const scene = 'login'; // 场景,不可更改
      const token = [this.appkey, new Date().getTime(), Math.random()].join(
        ':',
      );
      const option = {
        renderTo: `#${this.dynamicId}`, // 渲染到该DOM ID指定的Div位置
        appkey: this.appkey,
        scene: this.prefix + scene,
        token,
        language: this.alyLan,
        callback: (data) => {
          // 校验成功回调
          this.$emit('callback', {
            verificationType: '1',
            csessionid: data.csessionid,
            sig: data.sig,
            token,
            scene,
            nc,
          });
        },
        error() {
          // console.log('error', s);
        },
        verifycallback() {},
      };
      this.$nextTick(() => {
        if (bowser.mobile) {
          VueScript2.load(
            '//g.alicdn.com/sd/nch5/index.js?t=1497436353263',
          ).then(() => {
            window.NoCaptcha.init(option);
            window.NoCaptcha.setEnabled(true);
            nc = window.NoCaptcha;
          });
        } else {
          VueScript2.load('//g.alicdn.com/sd/ncpc/nc.js?t=1497440454594').then(
            () => {
              class F extends window.noCaptcha {}
              nc = new F();
              nc.init(option);
            },
          );
        }
      });
    },
  },
};
