result:
  .common-table {
    width: 100%;
    .mmd-loading-circle-right, .mmd-loading-circle-left {
      border-color: #1baaec;
    }
    .detailLoading {
      margin-right: 5px
    }
    table {
      width: 100%
      font-size: 12px;
    }
  }
  .table-head {
    padding: 0 20px;
    border-bottom-width: 1px ;
    border-bottom-style: solid;
    tr {
      vertical-align: middle;
    }
    th {
      text-align: right;
      vertical-align: middle;
      position: relative;
      &:first-child {
        text-align: left;
      }
    }
    .isSorte {
      cursor: pointer;
      user-select: none;
      display: inline-block;
      line-height: 14px;
      height: 14px;
    }
    .sorte {
      display: inline-block;
      float: right;
      width: 14spx;
      height: 14px;
      .icon {
        vertical-align: initial;
      }
      i {
        display: inline-block;
        width: 8px;
        height: 8px;
        position: absolute;
        left: 0;
        line-height: 6px;
        overflow: hidden;
        em {
          position: absolute;
          top: 0;
          right: 0;
          transition: all .3s ease-in-out;
          display: block;
          border-width: 4px;
          border-style: solid dashed dashed;
          border-color: #828ea1 transparent transparent;
          font-size: 0;
          line-height: 0;
          transform: rotate(180deg);
        }
        &.icon-triangle-up {
          bottom: 0;
          em {
            transform: rotate(0deg);
          }
        }
      }
      &.selected {
        .cur {
          em {
            border-color: #1baaec transparent transparent;
          }
        }
      }
    }
    .prompt {
      span {
        border-bottom-style: dashed;
        border-bottom-width: 1px;
        cursor: default;
      }
    }
    .promptText {
      position: absolute;
      display: none;
      width: 200px;
      padding: 20px;
      position: absolute;
      left: 0px;
      top: 26px;
      z-index: 10;
      box-shadow: 0px 0px 5px rgba(0,0,0,0.2);
      line-height: 1.5;
      text-align: left;
    }
    th {
      &:hover {
        .promptText {
          display: block;
        }
      }
    }
  }
  .table-body {
    min-height: 330px;
    border-radius: 0 0 2px 2px;
    .table-body-bar {
      padding: 0 20px;
    }
    tr {
      height: 50px;
      border-top-width: 1px ;
      border-top-style: solid;
      &:first-child {
        border-top-width: 0px;
      }
    }
    td {
      text-align: right;
      vertical-align: middle;
      line-height: 14px;
      &:first-child {
        text-align: left;
      }
      .label {
        line-height: 24px;
        display: inline-block;
        border-radius: 2px;
        text-align: center;
        padding: 0 10px;
      }
      .button, .link, .icon-btn {
        display: inline-block;
        vertical-align: middle;
        margin-left: 10px;
        line-height: 14px;
        cursor: pointer;
      }
      span {
        display: inline-block;
        line-height: 14px;
      }
      p {
        line-height: 14px;
      }
    }
    .t-select {
      position: relative;
      .icon {
        margin: 0;
      }
      &:hover {
        .select-box {
          display: block;
        }
      }
    }
    .inputext {
      line-height: 30px;
      width: 80px;
      padding: 0 10px;
      border-width: 1px;
      border-style: solid;
      border-radius: 3px;
    }
    .select-box {
      display: none;
      position: absolute;
      text-align: left;
      transform-origin: center top 0px;
      top: 16px;
      // left: 0;
      right: 0;
      z-index: 9;
      box-shadow: 0 3px 4px 1px rgba(0,0,0,0.28);
      border-radius: 2px;
      font-size: 14px;
      min-width: 120px;
      // overflow-y: scroll;
      padding: 10px 0px;
      .select-box-ul {
        max-height: 200px;
      }
      .select-option {
        line-height: 40px;
        padding: 0 20px;
        cursor: pointer;
        font-size: 12px;
        &:hover {
          background-color: #263043;
        }
      }
    }
    .sub-table-btn {
      cursor: pointer;
      position: relative;
      padding: 0 20px 0 10px;
      display: inline-block;
      height: 24px;
      line-height: 26px;
      border-radius: 2px;
      vertical-align: middle;
      user-select: none;
      .view-icon {
        position: absolute;
        top: 10px;
        right: 5px;
        display: block;
        border-width: 5px;
        border-style: solid dashed dashed;
        border-color: #1baaec transparent transparent;
        font-size: 0;
        line-height: 0;
      }
      &.isopen {
        .view-icon {
          transform: rotate(180deg);
          top: 5px;
        }
      }
    }
    .icon-btn {
      display: inline-block;
    }
    .sub-tr {
      border: none;
    }
  }
  .sub-table {
    padding: 0 20px
    tr {
      border: none;
      height: 40px;
      th {
        text-align: right;
        vertical-align: middle;
        width: 1%;
        &:first-child {
          text-align: left;
        }
      }
    }
    &.showSubTable {
      height: auto;
      transition: all 3s;
    }
  }
  .no-data {
    padding: 80px 0 80px;
    text-align: center;
    font-size: 12px;
    p {
      margin-top: 5px;
    }
    .icon {
      font-size: 36px;
    }
  }
  // 国际版
.Int {
  .table-head {
    padding: 0;
  }
