export default {
  model: {
    prop: 'value',
    event: 'change'
  },
  props: {
    value: {
      type: [String, Number],
      default: ''
    },
    options: {
      type: Array,
      default: () => []
    },
    showLabel: {
      type: Boolean,
      default: false
    }
  },
  data(){
    return{
      show: false,
      text: ''
    }
  },
  mounted() {
    if (this.showLabel) {
      this.text = this.options.filter(item => item.value === this.value)[0].label
    }
  },
  methods: {
    openValue() {
      this.show = !this.show;
    },
    getvalue(item) {
      this.text = item.label
      this.$emit('change', item.value ? item.value : item)
      this.show = false;
    },
  },
}
