.solidBc {
  position: absolute;
  width: 100%;
  height: 100%;
  left: 0;
  top: 0;
}
// 实心按钮
solid-style() {
  height: 40px;
  line-height: 40px;
}
// 实心按钮 -- 默认
.common-button-solid {
  font-size: 14px;
}

// 实心按钮 -- 禁止
.common-button-solid-disabled {
  font-size: 14px;
  cursor: not-allowed!important
}

// 实心按钮 -- 等待
.common-button-solid-loading {
  font-size: 14px;
  cursor: wait!important
}

// 空心按钮
.common-button-hollow, .common-button-hollow-big {
  border-width: 1px;
  border-style: solid;
}
.common-button-hollow {
  font-size: 12px
}
.common-button-hollow-big {
  font-size: 14px
}

// 文本按钮
.common-button-text,
.common-button-text-kind {
  font-size: 12px;
}
.common-button {
  transition: all 0.3s;
  box-sizing: border-box;
  border-radius: 2px;
  position: relative;
  overflow: hidden;
  text-align: center;
  cursor: pointer;
  user-select: none;
  display: inline-block;
  .common-button-slot {
    position: relative;
    display: inline;
    vertical-align: middle;
    .common-button-loading {
      position: absolute;
      vertical-align: middle;
      left: -14px;
      top: 50%;
      transform: translateY(-50%)
    }
  }
  .button-background {
    width: 100%;
    height: 100%;
    position: absolute;
    left: 0;
    top: 0;
    z-index: 2;
  }
}
.button-canv {
  position: absolute;
  left: 20px;
  top: 20px;
  transform: scale(0);
}
.mmd-waves-effect {
  border-radius: 100%;
  background-color: #D8D8D8;

}

.mmd-waves-effect-animation {
  animation: mmd-maves-animation-definition 0.8s ease-out;
  /* 兼容各大浏览器 */
  -moz-animation: mmd-maves-animation-definition 0.8s ease-out;
  -webkit-animation: mmd-maves-animation-definition 0.8s ease-out;
  -o-animation: mmd-maves-animation-definition 0.8s ease-out;
}

@keyframes mmd-maves-animation-definition {
  from {
    transform: scale(0.1);
    opacity: 0.5;
  }

  to {
    transform: scale(2); /* 因为涟漪的大小为标签的最长边，为了保证点击标签边缘时，涟漪也能覆盖整个标签，scale值最小应为2 */
    opacity: 0;
  }
}

